package exam02;

public class Operator02 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num1 = 10;
		int num2 = 3;
		
		System.err.println(num1 > num2); //10이 3보다 크니?
		System.err.println(num1 <= num2); // 10이 3 이하니?

		System.err.println(num1 == num2); //10이랑 3이 같니?
		System.err.println(num1 != num2); //10이랑 3이 다르니?
		
		boolean bo1 = num1 > num2;
		System.out.println(bo1);
		
	}

}
