package exam01;

public class operator06 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.println(10 & 11);
		System.out.println(10 | 11);
		System.out.println(10 ^ 11);
		System.out.println(~10); // -10-1
	
		System.out.println(10 >> 1);
		System.out.println(10 << 2);
	}

}
